mapper.select:
SELECT g.brand_code, b.brand_name, COUNT(*) AS order_count FROM order_info oi JOIN customer c ON oi.customer_code = c.customer_code JOIN goods g ON oi.goods_code = g.goods_code JOIN brand b ON g.brand_code = b.brand_code
WHERE c.customer_age BETWEEN #{startAge} AND #{endAge} AND oi.order_created_date BETWEEN #{startDate} AND #{endDate}
WHERE c.customer_age >= 60 AND oi.order_created_date BETWEEN #{startDate} AND #{endDate}
GROUP BY brand_code ORDER BY order_count DESC;
