mapper.select:
SELECT promotion_id, promotion_type_id, promotion_title, promotion_content, promotion_start_date, promotion_end_date, promotion_status FROM promotion WHERE 1=1
AND promotion_title LIKE CONCAT('%', trim(#{searchKeyword}), '%')
AND promotion_start_date BETWEEN #{startDate} AND #{endDate}
AND promotion_type_id = #{promotionTypeId}
AND promotion_Status = #{promotionStatus}

WITH yearly_sales AS ( SELECT p.promotion_title, EXTRACT(YEAR FROM p.promotion_start_date) AS promo_year, SUM(oi.order_price) AS total_sales FROM promotion p JOIN order_info oi ON oi.created_date BETWEEN p.promotion_start_date AND p.promotion_end_date WHERE p.promotion_id IN
#{type}
GROUP BY p.promotion_title, EXTRACT(YEAR FROM p.promotion_start_date)) SELECT promo_year, total_sales, LAG(total_sales) OVER (ORDER BY promo_year) AS prev_year_sales, IF(LAG(total_sales) OVER (ORDER BY promo_year) IS NOT NULL, ((total_sales - LAG(total_sales) OVER (ORDER BY promo_year)) / LAG(total_sales) OVER (ORDER BY promo_year) * 100), NULL) as growth_rate FROM yearly_sales ORDER BY promo_year
